mapper.select:
SELECT
*
FROM
ACCOUNT
WHERE
(
ACCOUNT.phone = #{phone} AND ACCOUNT.password  = #{password}
OR
ACCOUNT.username = #{username} AND ACCOUNT.password  = #{password}
OR
ACCOUNT.email = #{email} AND ACCOUNT.password = #{password}
)
AND
ACCOUNT.role = #{role}

mapper.update:
UPDATE
ACCOUNT
SET
ACCOUNT.phone = #{phone}
WHERE
ACCOUNT.id = #{id}
AND
ACCOUNT.role = #{role}

UPDATE
ACCOUNT
SET
ACCOUNT.email = #{email}
WHERE
ACCOUNT.id = #{id}
AND
ACCOUNT.role = #{role}

UPDATE
ACCOUNT
SET
ACCOUNT.nickname = #{nickname}
WHERE
ACCOUNT.id = #{id}

UPDATE
ACCOUNT
SET
ACCOUNT.username = #{username}
WHERE
ACCOUNT.id = #{id}
